import React, { useState } from "react";

function Number() {
  const [number, setNumber] = useState(0);

  const plusCounter = () => {
    setNumber((prevstate) => prevstate + 1);
  };
  const minusCounter = () => {
    setNumber((prevstate) => prevstate - 1);
  };

  return (
    <div>
      <button onClick={plusCounter}> + </button>
      <p>{number}</p>
      <button onClick={minusCounter}> - </button>
    </div>
  );
}

export default Number;
