import React, { useEffect, useState } from "react";
import CardItem from "./CardItem";
import axios from "axios";

function Card({ filter }) {
  const [data, setData] = useState([]);
  console.log(filter);
  useEffect(() => {
    const fetch = async () => {
      await axios
        .get("https://restcountries.com/v3.1/all")
        .then((res) => {
          setData(res.data);
        })
        .catch((err) => console.log(err));
    };
    fetch();
  }, []);
  return (
    <div>
      <div className="card ">
        {data.map(
          (item) => {
            if (filter === "Filter By Region") {
              console.log("inja");
              <CardItem items={item} key={item.name.common} />;
              console.log("onja");
            } else if (filter === item.region) {
              <CardItem items={item} key={item.name.common} />;
            }
          }
          // filter === "Filter By Region" ? (
          //   <CardItem {...item} key={item.name.common} />
          // ) : (
          //   <CardItem {...item} key={item.name.common} />
          // )
        )}
      </div>
    </div>
  );
}

export default Card;
