import React, { useEffect, useState } from "react";
import CardItem from "./CardItem";
import axios from "axios";

function Card({ filter, country }) {
  const [data, setData] = useState([]);

  useEffect(() => {
    const fetch = async () => {
      await axios
        .get("https://restcountries.com/v3.1/all")
        .then((res) => {
          setData(res.data);
        })
        .catch((err) => console.log(err));
    };
    fetch();
  }, []);

  return (
    <div>
      <div className="card ">
        {data.map((item) => {
          if (country && country === item.name.common.toLowerCase()) {
            console.log(country, item.name.common);

            return <CardItem {...item} key={item.name.common} />;
          } else if (filter === "Filter By Region")
            return <CardItem {...item} key={item.name.common} />;
          else if (filter === item.region)
            return <CardItem {...item} key={item.name.common} />;
        })}
      </div>
    </div>
  );
}

export default Card;
