import React, { useEffect, useState } from "react";
import Card from "./Card";
import "./Content.css";
import axios from "axios";

function Content() {
  const [filter, SetFilter] = useState("");

  const [data, setData] = useState([
    "Africa",
    "Asia",
    "America",
    "Oceania",
    "Europe",
