  return (
    <div>
      <div className="content">
        <div className="above">
          <div className="above-content col-sm-12 col-md-4">
            <img src="/img/search.svg" alt="search"></img>
            <input type="text" placeholder="Serach for a Country ...."></input>
          </div>
          <div
            className="select-box col-sm-6 col-md-2"
            onClick={() => addclass()}
          >
            <div className="labelfilter">
              {/* <label>Filter by Region</label>
              <img src="./img/down.svg" alt="down"></img> */}
              <select>
                <option>Filter by Region</option>
                {data.map((item) => {
                  return (
                    <option key={item} onChange={() => SetFilter(item)}>
                      {item}
                    </option>
                  );
                })}
              </select>
            </div>
            <div className="ulfilter">
              {/* <ul className="country">
                {data.map((item) => {
                  return <li>{item.region}</li>;
                })}
                <li>ddd</li>
                <li>ddd</li>
                <li>ddd</li>
                <li>ddd</li>
              </ul> */}
