                <option>Filter by Region</option>
                {data.map((item) => {
                  return <option>{item.region}</option>;
                })}
              </select>
            </div>
          </div>
        </div>
        <div className="down">
          <Card />
        </div>
      </div>
    </div>
  );
}

export default Content;
